// --- 2. NAVBAR SCROLL EFFECT ---
const nav = document.querySelector('nav');
if(nav) {
    window.addEventListener('scroll', () => {
        if(window.scrollY > 50) {
            nav.classList.add('black');
        } else {
            nav.classList.remove('black');
        }
    });
}

// --- 3. RENDER SLIDERS (Home Page) ---
function renderSliders() {
    // Unique Categories
    const categories = [...new Set(movies.map(m => m.category))];
    const container = document.getElementById('slider-container');
    if(!container) return;

    categories.forEach(cat => {
        const row = document.createElement('div');
        row.className = 'row';
        
        const filteredMovies = movies.filter(m => m.category === cat);
        const postersHtml = filteredMovies.map(m => `
            <img src="${m.img}" class="poster" onclick="playMovie(${m.id})">
        `).join('');

        row.innerHTML = `
            <h2 class="row-title">${cat}</h2>
            <div class="row-posters">
                ${postersHtml}
            </div>
        `;
        container.appendChild(row);
    });
}

// --- 4. PLAY MOVIE (Navigation) ---
function playMovie(id) {
    window.location.href = `play.html?id=${id}`;
}

// --- 5. GRID RENDER (Movies/Search Page) ---
function renderGrid(containerId, filterText = '') {
    const container = document.getElementById(containerId);
    if(!container) return;

    let data = movies;
    if(filterText) {
        data = movies.filter(m => m.title.toLowerCase().includes(filterText.toLowerCase()));
    }

    container.innerHTML = data.map(m => `
        <div style="position:relative;">
            <img src="${m.img}" style="width:100%; border-radius:4px; cursor:pointer; transition:0.3s;" onclick="playMovie(${m.id})">
            <div style="margin-top:5px; font-size:14px; font-weight:bold;">${m.title}</div>
            <button onclick="toggleWatchlist(${m.id})" style="background:#333; color:white; border:none; padding:5px 10px; margin-top:5px; cursor:pointer; border-radius:2px; font-size:12px;">
                ${isInWatchlist(m.id) ? '- Remove' : '+ List'}
            </button>
        </div>
    `).join('');
}

// --- 6. WATCHLIST LOGIC ---
function toggleWatchlist(id) {
    let list = JSON.parse(localStorage.getItem('myList')) || [];
    if(list.includes(id)) {
        list = list.filter(itemId => itemId !== id);
        alert("Removed from list");
    } else {
        list.push(id);
        alert("Added to list");
    }
    localStorage.setItem('myList', JSON.stringify(list));
    // Refresh if on watchlist page or grid
    if(window.location.href.includes('watchlist') || window.location.href.includes('movies')) {
        location.reload();
    }
}

function isInWatchlist(id) {
    let list = JSON.parse(localStorage.getItem('myList')) || [];
    return list.includes(id);
}

function loadWatchlist() {
    const container = document.getElementById('watchlist-grid');
    if(!container) return;
    
    let listIds = JSON.parse(localStorage.getItem('myList')) || [];
    let listMovies = movies.filter(m => listIds.includes(m.id));

    if(listMovies.length === 0) {
        container.innerHTML = "<p>Your list is empty.</p>";
        return;
    }
    
    // Reuse render logic manually
    container.innerHTML = listMovies.map(m => `
        <div>
            <img src="${m.img}" style="width:100%; border-radius:4px; cursor:pointer;" onclick="playMovie(${m.id})">
            <div style="margin-top:5px; font-weight:bold;">${m.title}</div>
            <button onclick="toggleWatchlist(${m.id})" style="background:var(--primary); color:white; border:none; padding:5px 10px; margin-top:5px; cursor:pointer; border-radius:2px; font-size:12px;">Remove</button>
        </div>
    `).join('');
}